function formDevChart(data) {
    var url = "https://chart.googleapis.com/chart?chm=N,000000,1,-1,10&chdl=Minimal Time|Actual Time&chtt={3}&cht=lc&chco=FF0000,00FF00&chs=1000x300&chds=a&chd=t:{0}|{1}&chxt=x,y&chxl=0:{2}1:||1||2||3||4||5||6||7||8";
    var scaleXStr = "|";
    var minimalTimeUrlPart = "";
    var actualTimeUrlPart = "";
    var developerName = "Pavlo";
    for (var datesI = 0; datesI < data.length; datesI++) {
        var curDateArray = data[datesI];
        var curDate = null;
        var isLast = (datesI == (data.length - 1));
        for (var devsI = 0; devsI < curDateArray.length; devsI++) {
            var curDev = curDateArray[devsI];
            if (curDev.developer.indexOf("Pavlo") == -1) continue;
            var developerName = curDev.developer;
            curDate = curDev.date;

            minimalTimeUrlPart += curDev.stats.minimalTime + (isLast ? "" : ",");
            actualTimeUrlPart += curDev.stats.actual.toFixed(2) + (isLast ? "" : ",");
        }
        var d = new Date(curDate);
        scaleXStr += d.getDate() + /* "-" + (d.getMonth() + 1) + */"|";
    }
    Logger.log(url.format(minimalTimeUrlPart,actualTimeUrlPart, scaleXStr, developerName));
}

function getTasksByProjects(data) {
    var result = {};
    for (var key in data) {
        if (!data.hasOwnProperty(key) || !data[key].tasks) continue;
        var tasks = data[key].tasks;
        for (var i = 0; i < tasks.length; i++) {
            var projectTasksArray = result[tasks[i].project] || [];
            var projectTaskEntry = tasks[i];
            projectTaskEntry.developer = key;
            projectTasksArray.push(projectTaskEntry);
            result[tasks[i].project] = projectTasksArray;
        }
    }
    return result;
}

function toNumber(str) {
    var parsed = parseFloat(str);
    return isNaN(parsed) ? 0 : parsed;
}

function prepareStatistics(data) {
    /*
     developerStatsFormat = {
     date: null,
     name: "",
     numberOfTasks: 0,
     estimated: 0,
     notInvoiced: 0,
     actual: 0
     };
     */
    var developerStatsArray = [];

    for (var dayCounter = 0; dayCounter < data.length; dayCounter++) {
        var date = data[dayCounter].date;
        var sheetData =  data[dayCounter].sheetData;
        developerStatsArray.push([]);
        for (var devKey in sheetData) {
            if (!sheetData.hasOwnProperty(devKey)) continue;
            var currentDev = sheetData[devKey];
            var currentDeveloperStats = {developer: devKey, date: date, stats: {
                dateRow: dayCounter,
                minimalTime: currentDev.minimalTime,
                numberOfTasks: 0,
                estimated: 0,
                notInvoiced: 0,
                actual: 0
            }};
            developerStatsArray[dayCounter].push(currentDeveloperStats);
            var tasks = currentDev.tasks;
            var s = currentDeveloperStats.stats;
            s.numberOfTasks = tasks.length;
            for (var i = 0; i < tasks.length; i++) {
                var projectTaskEntry = tasks[i];
                s.estimated += toNumber(projectTaskEntry.estimate);
                s.notInvoiced += toNumber(projectTaskEntry.notInvoiced);
                s.actual += toNumber(projectTaskEntry.actual);
            }
        }
    }
    return {devStats: developerStatsArray};
}


